mapper.update:
UPDATE send_alarm
   SET alarm_check = true
 WHERE user_no = #{userNo}
   AND seller_page_no = #{sellerPageNo}

mapper.select:
SELECT NICKNAME,
	   sub_date
FROM user_alarm
JOIN member using(user_no)
WHERE seller_page_no = (SELECT s.seller_page_no
					    FROM seller_page s
						JOIN seller_info i using(business_no)
						WHERE i.user_no = #{userNo}
						);
