mapper.update:
UPDATE	BOARD
SET		BOARD_TITLE = #{boardTitle},
		BOARD_LEVEL = #{boardLevel},
		BOARD_CONTENTS = #{boardContents}
WHERE	BOARD_NO = #{boardNo}

UPDATE	BOARD
SET		STATUS = 'N'
WHERE	BOARD_NO = #{boardNo}

UPDATE	BOARD_REPLY
SET		STATUS = 'N'
WHERE	BOARD_REPLY_NO = #{boardReplyNo}

mapper.select:
SELECT	COUNT(*)
FROM	BOARD_THUMBUP
WHERE	BOARD_NO = #{boardNo}

SELECT	COUNT(*)
FROM	BOARD_THUMBUP
WHERE	BOARD_NO = #{boardNo}
AND		USER_NO = #{userNo}
