mapper.select:
SELECT COUNT(*) 
FROM public.fish
WHERE fish_name = #{fishName}

SELECT COUNT(*) 
FROM public.fish
WHERE fish_type = #{cate}

		SELECT fish_no,
			   fish_name,
			   origin_name
		FROM public.fish
		JOIN public.fish_attachment using(fish_file_no)
		WHERE fish_type = #{fishType} 
          AND tendency = #{tendency} 
          AND taste_type = #{tasteType}
