mapper.update:
UPDATE member
SET activated = 'N'
WHERE user_no = #{userNo}

mapper.select:
SELECT authority
FROM member
WHERE user_no = #{userNo}

SELECT status
FROM seller_info
WHERE user_no = #{userNo}

SELECT	COUNT(*)
FROM	LOVE_IT
WHERE	USER_NO = #{userNo}

mapper.delete:
DELETE FROM LOVE_IT
WHERE	USER_NO = #{userNo}
AND		P_NO = #{prNo}
